import React from "react";
import { useEffect } from "react";
import { Line } from "react-chartjs-2";
import axios from "axios";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Filler,
  Legend,
} from "chart.js";
import { useDispatch } from "react-redux";
import { useParams } from "react-router-dom";
import { IGraph } from "./Igraph";
import { setList } from "../../app/reducers/weatherreport/graphSlice";
import { useAppSelector } from "../../app/hook";
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Filler,
  Legend
);
const options = {
  responsive: true,
  plugins: {
    legend: {
      position: "top" as const,
    },
    title: {
      display: true,
      text: "",
    },
  },
};
interface Iprop{
yaxis: number[];
xaxis: string[];
}
const Graph = (props: Iprop) => {

  const list = useAppSelector((state) => state.graph.list);
  const labels = props.xaxis.map((item)=>item)

  const weatdata = {
    labels,
    datasets: [
      {
        fill: true,
        data: props,
        borderColor: "rgb(53, 162, 235)",
        backgroundColor: "rgba(53, 162, 235, 0.5)",
        pointRadius: 3,
      },
    ],
  };
  return (
    <>
      <Line options={options} data={weatdata} />
    </>
  );
};
export default Graph;
