import React from 'react'
import { useEffect } from "react";
import { Line } from "react-chartjs-2";
import axios from "axios";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Filler,
  Legend,
} from "chart.js";
import { useDispatch } from 'react-redux';
import { useParams } from 'react-router-dom';
import { IGraph } from './Igraph';
import { setList } from '../../app/reducers/weatherreport/graphSlice';
import { useAppSelector } from '../../app/hook';
ChartJS.register(
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Filler,
    Legend
  );
  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: "top" as const,
      },
      title: {
        display: true,
        text: "",
      },
    },
  };
const Graph =() => {
    const apiKey ="019bbaf0caaa302418a19bbe6cb2ff84";
    const dispatcher= useDispatch();
    const params = useParams();

    const fiveDayForecast = async() =>{
    try{
        const res = await axios.get(`http://api.openweathermap.org/data/2.5/forecast?q=${params.city}&appid=${apiKey}`);
        let data: IGraph =res.data;
        dispatcher(setList(data.list));
    }catch (error) {
        console.log(error);
      }
    }
    useEffect(() => {
        fiveDayForecast();
      }, []);
      const  list  = useAppSelector((state) => state.graph.list);
      const labels =list.map((item) => {
        const date = new Date(item.dt * 1000);
        return (
            date.toLocaleString("en-US", { day: "numeric" }) +
            " " +
            date.toLocaleString("en-US", { month: "long" })
        );
      })
      const weatdata = {
        labels,
        datasets: [
            {
                fill: true,
                data: list.map((item) => {
                    return parseFloat((item.main.temp - 273.15).toFixed(1));
                  }),
                borderColor: 'rgb(53, 162, 235)',
                backgroundColor: 'rgba(53, 162, 235, 0.5)',
                pointRadius: 3,
            },
        ],
    };
  return (
    <>
  <Line  options={options} data={weatdata} />
    </>
  )
}

export default Graph
